import React, { useState } from 'react';
import CurrentSong from './components/CurrentSong';
import AddSong from './components/AddSong';
import FullList from './components/FullList';

export default function App(){
  const [allSongs,updateAllSongs] = useState([])
  const [display, setDisplay] = useState(false)
  const [text,changeText] = useState("")
  function onChange(e){
    changeText(prev=>e.target.value)
  }
  async function onSubmit(e) {
    e.preventDefault();
    // When a post request is sent to the create url, we'll add a new record to the database.
    let songTitle= document.getElementById('addedSong').value
    const newSong = {
      name:songTitle,
      count:0
    };
    await fetch("http://localhost:5000/record/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newSong),
    })
    .catch(error => {
      window.alert(error);
    });

    window.alert(`You have added ${newSong.name} to the list`)
    changeText("")
  }
  return(
    <>
      <CurrentSong />
      <AddSong onSubmit = {onSubmit} onChange = {onChange} text={text}/>
      <FullList allSongs={allSongs} updateAllSongs={updateAllSongs} display={display} setDisplay={setDisplay}/>
    </>
  )
}
