import React, { useState } from 'react';

export default function AddSong(props){
  const [text,changeText] = useState("")


  function onChange(e){
    changeText(prev=>e.target.value)
  }
  return(
    <div className = 'addSong'>
    <h1>Add a Song</h1>
    <form onSubmit ={()=>props.onSubmit(text)}>///
    <input id='addedSong' type='text' value = {text} onChange = {onChange} placeholder ='type title here'/>
    <button className='addButton' type="submit">Submit</button>
    </form>
    </div>
  )
}
