import React, { useState } from 'react';

export default function AddSong(){
  const [text,changeText] = useState("")

  async function onSubmit(e) {
    e.preventDefault();

   // When a post request is sent to the create url, we'll add a new record to the database.
   //create object using 'computed property names'
   const newSong = {[text]:0};

   await fetch("http://localhost:3000/record/add", {
     method: "POST",
     headers: {
       "Content-Type": "application/json",
     },
     body: JSON.stringify(newSong),
   })
   .catch(error => {
     window.alert(error);
     return;
   });

   changeText("");
   // navigate("/");
  }
  function onChange(e){
    changeText(prev=>e.target.value)
  }
  return(
    <div className = 'addSong'>
      <h1>Add a Song</h1>
      <form onSubmit ={onSubmit}>
        <input type='text' value = {text} onChange = {onChange} placeholder ='type title here'/>
        <button className='addButton' type="submit">Submit</button>
      </form>
    </div>
  )
}
