import React, { useState } from 'react';

export default function AddSong({setNumSongs, setDisplay}){
  const [text,changeText] = useState("")

  function onChange(e){
    changeText(prev=>e.target.value)
  }
  async function onSubmit(e) {
    e.preventDefault();
    // When a post request is sent to the create url, we'll add a new record to the database.
    const inputEl = document.getElementById('addedSong')
    let songTitle= inputEl.value
    const newSong = {
      name:songTitle,
      instrument:"guitar",
      count:0
    };
    await fetch("http://localhost:5000/record/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newSong),
    })
    .catch(error => {
      window.alert(error);
    });
    changeText("")
    inputEl.focus()
    setNumSongs(prev=>prev+1)
    setDisplay(true)
  }

  return(
    <div className = 'addSong'>
      <h1>Add a Song</h1>
      <form onSubmit = {onSubmit}>
        <input id='addedSong' type='text' value = {text} onChange = {onChange} placeholder ='type title here'/>
        <button className='addButton'>Submit</button>
      </form>
    </div>
  )
}
