import React, { useState } from 'react';
export default function CurrentSong({allSongs}){
  const [title,newTitle] = useState('Let me see...')

  async function chooseSong(){
    const instrumentSongs = allSongs.filter(song=>song.instrument === instrument)

    const eligibleSongs = instrumentSongs.filter(song=>song.count < 4)

    if(eligibleSongs.length > 0){
      let index = Math.floor(Math.random() * eligibleSongs.length)
      let ti = eligibleSongs[index].name
      eligibleSongs[index].count+=1;
      fetch(`http://localhost:5000/update/${eligibleSongs[index]._id}`,{
        method:'POST',
        body:JSON.stringify(eligibleSongs[index]),
        headers: {
          'Content-Type': 'application/json'
        },
      })
      .then(newTitle(ti))
      return;
    }else{
      for(let song in allSongs){
        allSongs[song].count=0
        await fetch(`http://localhost:5000/update/${allSongs[song]._id}`,{
          method:'POST',
          body:JSON.stringify(allSongs[song]),
          headers: {
            'Content-Type': 'application/json'
          },
        })
      }
      newTitle('resetting...')
      chooseSong()
    }
  }
  return(
    <>
      <div className = 'next'>
        <button id='getSong' onClick = { chooseSong }>What Song to Play Next?</button>
        <h1>{title}</h1>
      </div>
    </>

  )
}
