import React, {useState, useEffect} from 'react';
import CurrentSong from './components/CurrentSong';
import AddSong from './components/AddSong';
import FullList from './components/FullList';

export default function App(){  
  const [display, setDisplay] = useState(false)
  // const [numSongs, setNumSongs] = useState(0)
  const [allSongs,updateAllSongs] = useState([])
  const [instrument, setInstrument] = useState('guitar')

  useEffect(()=>{
    fetch(`http://localhost:5000/record/`)
      .then(response=>{
        if(!response.ok){
          window.alert(`An error occurred: ${response.statusText}`)
          return []
          }         
          return response.json()
      })
        .then(result => {
          updateAllSongs(result);
        })
          .catch(error => console.error("Fetch error:", error));
    },[allSongs])

  function changeInstrument(){
    setInstrument(prev=>prev === 'guitar' ? 'harp' : 'guitar')
  }

  return(
    <>
      {<button onClick={()=>changeInstrument()}>Go to {instrument==="guitar"?"harp":"guitar"} page</button> }
      <CurrentSong />
      <AddSong setDisplay={setDisplay} updateAllSongs = {updateAllSongs}  instrument = {instrument}/>
      <FullList allSongs = {allSongs} updateAllSongs = {updateAllSongs} display = {display} setDisplay={setDisplay} n/>
    </>
  )
}
