import React, {useState, useEffect} from 'react';
import CurrentSong from './components/CurrentSong';
import AddSong from './components/AddSong';
import FullList from './components/FullList';
// import { use } from '../../../experimental_sandbox/backupofsongpickermern/songpicker_mern/server/routes/record';

export default function App(){  
  const [display, setDisplay] = useState(false)
  const [numSongs, setNumSongs] = useState(0)
  const [allSongs,updateAllSongs] = useState([])
  const [instrument, setInstrument] = useState('guitar')
  const [username, setUsername] = useState('')
  const [newUser, setNewUser] = useState('')

  const [loggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    document.body.className = instrument;
  }, [instrument]);

  function changeInstrument(){
    setInstrument(prev=>prev === 'guitar' ? 'harp' : 'guitar')
    setDisplay(false)
  }
  function logIn(){

    setLoggedIn(true);
  }
  function signUp(e){
    e.preventDefault();
    console.log(newUser);
    // fetch(`http://localhost:5000/signUp`,{
    //     method:'POST',
    //     body:JSON.stringify(eligibleSongs[index]),
    //     headers: {
    //       'Content-Type': 'application/json'
    //     },
    //   })
    // setLoggedIn(true);
  }

  function logIn(e){
    e.preventDefault();
    // setUsername(username);
    console.log('b')
    setLoggedIn(true);
  }

  function handleUserChange(e){
    setUsername(e.target.value)
  }
  function handleNewUserChange(e){
    setNewUser(e.target.value)
  }

  return(
   loggedIn ? (<>
      <CurrentSong allSongs = {allSongs} instrument={instrument} changeInstrument={changeInstrument}/>
      <AddSong setDisplay={setDisplay} updateAllSongs = {updateAllSongs} setNumSongs = {setNumSongs} instrument = {instrument}/>
      <FullList allSongs = {allSongs} updateAllSongs = {updateAllSongs} display = {display} setDisplay={setDisplay} numSongs = {numSongs} setNumSongs = {setNumSongs} instrument={instrument} changeInstrument={changeInstrument}/>
    </>):

    (<>
      <h1>Please log in or sign up to access the song picker.</h1>
      <form onSubmit={logIn}>     
        <input onChange = {handleUserChange} value = {username} type="text" placeholder="Username" />
        <button >Log In</button>
      </form>
      <p>Don't have an account? Sign up here!</p>
      <form onSubmit={signUp}>
        <input onChange={handleNewUserChange} value = {newUser} type="text" placeholder="Username" />
        <button >Sign Up</button>
      </form>
    </>)

  )
}
