import React, {useState} from 'react';
import CurrentSong from './components/CurrentSong';
import AddSong from './components/AddSong';
import FullList from './components/FullList';

export default function App(){  
  const [display, setDisplay] = useState(false)
  const [numSongs, setNumSongs] = useState(0)
  const [allSongs,updateAllSongs] = useState([])
  const [instrument, setInstrument] = useState('guitar')
  return(
    <>
      {instrument === 'guitar' ? <button>Go to harp page</button> : <button>Go to guitar page</button>}
      <CurrentSong />
      <AddSong setDisplay={setDisplay} updateAllSongs = {updateAllSongs} setNumSongs = {setNumSongs} instrument = {instrument}/>
      <FullList allSongs = {allSongs} updateAllSongs = {updateAllSongs} display = {display} setDisplay={setDisplay} numSongs = {numSongs} setNumSongs = {setNumSongs}/>
    </>
  )
}
