export default function FullList({allSongs, updateAllSongs, display, setDisplay}){
  const [displayValue, setDisplayValue] = useState(false)
  function showList(){
      setDisplayValue(true)
      document.getElementById('fullList').style='display:block'
      console.log("calling showList again!")
    }


    useEffect(()=>{
    fetch(`http://localhost:5000/record/`)
      .then(response=>response.ok?response.json():window.alert(`An error occurred: ${response.statusText}`)
      .then(result => {
        console.log("inside useEffect")
        updateAllSongs(result)})
    )},[displayValue])


  function hideList(){
    setDisplayValue(false)
    document.getElementById('fullList').style='display:none'
  }

  async function deleteSong(i){
    await fetch(`http://localhost:5000/${allSongs[i]._id}`,{
    method:"DELETE"
  })
    showList()
  }

  const nl = allSongs.map((n,i)=><li key ={i}>{`${n.name}`}<button className = 'delete' onClick = {()=>deleteSong(i)}>Delete</button></li>)
  if(displayValue){
    // showList()
  }
  return(
    <>
    {displayValue?<button id = 'listToggle' onClick =  { hideList }>Hide List</button>:<button id = 'listToggle' onClick={ showList }>See them all</button>}
    <div id = 'listDiv'>
      <ul id="fullList">{nl}</ul>
    </div>
    </>
  )
}
