import React, { useState, useEffect } from 'react';
export default function CurrentSong(){
  const [title,newTitle] = useState('Let me see...')
  const [songList,updateSongList] = useState([])

  // useEffect(()=>{
  //     fetch(`http://localhost:5000/record`)
  //       .then(response=>response.ok?response.json():window.alert(`An error occurred: ${response.statusText}`)
  //     )
  //     .then(result => updateSongList(result))
  //   },[title]
  // )

  async function chooseSong(){
    const eligibleSongs = songList.map(song=>{
        if(song.count<4){
          return song;
        }else{
          return undefined
        }
      }).filter(el=>el!==undefined)
      console.log('eligibleSongs:',eligibleSongs)
    if(eligibleSongs.length > 0){
      let index = Math.floor(Math.random() * eligibleSongs.length)
      let ti = eligibleSongs[index].name
      eligibleSongs[index].count+=1;
      fetch(`http://localhost:5000/update/${eligibleSongs[index]._id}`,{
        method:'POST',
        body:JSON.stringify(eligibleSongs[index]),
        headers: {
          'Content-Type': 'application/json'
        },
      })
      .then(newTitle(ti))
      return;
    }else{
      for(let song in songList){
        songList[song].count=0
        await fetch(`http://localhost:5000/update/${songList[song]._id}`,{
          method:'POST',
          body:JSON.stringify(songList[song]),
          headers: {
            'Content-Type': 'application/json'
          },
        })
      }
      newTitle('resetting...')
      chooseSong()
    }
  }
  return(
    <>
      <div className = 'next'>
        <button id='getSong' onClick = { chooseSong }>What Song to Play Next?</button>
        <h1>{title}</h1>
       </div>
    </>

  )
}
