          return song;
        }
      }).filter(el=>el!=undefined)
      console.log(eligibleSongs)

    if(eligibleSongs.length > 0){
      let index = Math.floor(Math.random() * eligibleSongs.length)
      let ti = eligibleSongs[index].name

    //  updateDataBase(title or songList?)//?????????????????????
        newTitle(ti)
        console.log('okay, I am returning')
        return;
      }else{
        for(let song in songList){
          song.count=0
        }
  //  updateDataBase(title or songList?)//?????????????????????
         getSong()
      }
    }
  },[fetchSong])

  // getSong()
  return(
    <div className = 'next'>
      <button id='getSong' onClick={ addToFetchSong }>What Song to Play Next?</button>
      <h1>{title}</h1>
    </div>
  )
}
