import React, { useState } from 'react';

export default function FullList(){
  const [allSongs,updateAllSongs] = useState([])
  const [display, setDisplay] = useState(false)

  function showList(){
      setDisplay(true)
      fetch(`http://localhost:5000/record/`)
        .then(response=>response.ok?response.json():window.alert(`An error occurred: ${response.statusText}`)
      )
      .then(result => updateAllSongs(result))
  }

  const nl = allSongs.map((n,i)=><li>{`${n.name}`}<button>Delete</button></li>)

  return(
    <div id = 'listDiv'>
    {!display?<button onClick = { showList }>See them all</button>:<button onClick={()=>setDisplay(true)}>Hide List</button>}
      <ul>{nl}</ul>
    </div>
  )
}
