import React, { useState, useEffect } from 'react';

export default function FullList({allSongs, updateAllSongs, display, setDisplay}){
    useEffect(()=>{
    fetch(`http://localhost:5000/record/`)
      .then(response=>{
        if(!response.ok){
          window.alert(`An error occurred: ${response.statusText}`)
          return []
          }         
          return response.json()
      })
        .then(result => {
          updateAllSongs(result);
        })
          .catch(error => console.error("Fetch error:", error));
    },[display])

  async function deleteSong(i){
    await fetch(`http://localhost:5000/${allSongs[i]._id}`,{
    method:"DELETE"
  })
    setDisplay(true)
  }

  const nl = allSongs.map((n,i)=><li key ={i}>{`${n.name}`}<button className = 'delete' onClick = {()=>deleteSong(i)}>Delete</button></li>)

  return(
    <>
    {display?<button id = 'listToggle' onClick =  {()=>setDisplay(false) }>Hide List</button>:<button id = 'listToggle' onClick={ ()=>setDisplay(true) }>See them all</button>}
    <div id = 'listDiv'>
      {display && <ul id="fullList">{nl}</ul>}
    </div>
    </>
  )
}
