    const response = await fetch(`http://localhost:5000/record/`)
    if (!response.ok) {
      const message = `An error occurred: ${response.statusText}`;
      window.alert(message);
      return;
    }
    const songList = await response.json()
    const songs = Object.keys(songList);
    console.log(Object.keys(songList[0])[1])
  //continue the process as long as there is at least 1 song with fewer than 4 plays
    while(Math.min(...Object.values(songList))<4){
      let index = Math.floor(Math.random() * songs.length)
      let ti = songs[index]
      if(songList[ti] < 4){
        songList[ti]+=1;

    //  updateDataBase(title or songList?)//?????????????????????
        newTitle(ti)
        return;
      }else{
        for(let song in songList){
          songList[song]=0
        }
  //  updateDataBase(title or songList?)//?????????????????????
        getSong()
      }
    }
  }
  getSong()
  return(
    <div className = 'next'>
      <button id='getSong' onClick={ getSong }>What Song to Play Next?</button>
      <h1>{title}</h1>
    </div>
  )
}
