import React, { useEffect } from 'react';

export default function FullList({allSongs, updateAllSongs, display, setDisplay, numSongs, setNumSongs, instrument}){
    useEffect(()=>{
    fetch(`http://localhost:5000/record/`)
      .then(response=>{
        if(!response.ok){
          window.alert(`An error occurred: ${response.statusText}`)
          return []
          }         
          return response.json()
      })
        .then(result => {
          updateAllSongs(result);
        })
          .catch(error => console.error("Fetch error:", error));
    },[numSongs])

  async function deleteSong(i){
    await fetch(`http://localhost:5000/${instrumentSongs[i]._id}`,{
    method:"DELETE"
  })
    setNumSongs(prev=>prev - 1) 
    setDisplay(true)
  }
  const instrumentSongs = allSongs.filter(song=>song.instrument === instrument)
  const nl = instrumentSongs.map((n,i)=><li key ={i}>{`${n.name}`}<button className = 'delete' onClick = {()=>deleteSong(i)}>Delete</button></li>)

  return(
    <>
      {display?<button id = 'listToggle' onClick =  {()=>{ console.log("setting to false");setDisplay(false)} }>Hide List</button>:<button id = 'listToggle' onClick={ ()=>{ console.log("setting to true");setDisplay(true)} }>See them all</button>}
      <div id = 'listDiv'>
        {display && <ul id="fullList">{nl}</ul>}
      </div>
    </>
  )
}
